mapper.select:
SELECT

from approve_record_his
where approve_id = #{approveId}
and approve_type = #{approveType}
and approve_step = #{approveStep}
and approve_end_date is NULL
order by approve_start_date desc
limit 1

SELECT

from approve_record_his
where approve_end_date is not NULL
and approve_id = #{approveId}
and approve_type = #{approveType}
ORDER by approve_start_date ASC
