mapper.select:
select 

from quote_mold_prod_log
where id = #{id,jdbcType=BIGINT}
  and version = #{version,jdbcType=INTEGER}

mapper.delete:
delete from quote_mold_prod_log
where id = #{id,jdbcType=BIGINT}
  and version = #{version,jdbcType=INTEGER}

mapper.update:
update quote_mold_prod_log
set quote_mold_id = #{quoteMoldId,jdbcType=BIGINT},
  cst_prod_name = #{cstProdName,jdbcType=VARCHAR},
  cst_prod_no = #{cstProdNo,jdbcType=VARCHAR},
  cavity_total = #{cavityTotal,jdbcType=INTEGER},
  prod_model_pic = #{prodModelPic,jdbcType=VARCHAR},
  status = #{status,jdbcType=TINYINT}
where id = #{id,jdbcType=BIGINT}
  and version = #{version,jdbcType=INTEGER}
