mapper.insert:
insert into quote_payment_term (id, version, quote_id,
quote_no, seq_no, pay_ratio,
pay_money, pay_term, status
)
values (#{id,jdbcType=BIGINT}, #{version,jdbcType=INTEGER}, #{quoteId,jdbcType=BIGINT},
#{quoteNo,jdbcType=VARCHAR}, #{seqNo,jdbcType=INTEGER}, #{payRatio,jdbcType=DECIMAL},
#{payMoney,jdbcType=DECIMAL}, #{payTerm,jdbcType=VARCHAR}, #{status,jdbcType=TINYINT}
)

mapper.select:
SELECT

from quote_payment_term_log
where status = 1
and quote_id = #{quoteId}
and version = #{version}
ORDER by seq_no ASC
