mapper.select:
select id as calendar_id,calendar_name
from pm_calendar
where status = 1
and company_id = #{companyId}
and (#{query.calendar_name} is null or #{query.calendar_name} = '' or calendar_name LIKE "%"#{query.calendar_name}"%")
limit #{page},#{pageSize}

select distinct pc.id as calendar_id, pc.calendar_name
from pm_calendar as pc INNER JOIN pm_calendar_sub as pcs on pc.id = pcs.calendar_id
where pc.id = #{calendar_id} and pc.status = 1 and pcs.status = 1

  and pcs.the_date >= #{start_date}


  and pcs.the_date <= #{end_date}

mapper.update:
update pm_calendar
SET status = 0, updated_at = #{updateAt}
where id = #{calendarId}
