mapper.select:
select

from pm_routing_tmpl WHERE 1=1

  and company_id = #{company_id}


  and tmpl_type_id = #{tmpl_type_id}


  and tmpl_name LIKE  CONCAT('%','${tmpl_name}','%' )


  and tmpl_type_name LIKE  CONCAT('%','${tmpl_type_name}','%' )


  and status = #{status}

limit #{page}, #{page_size}

SELECT count(1)
from pm_routing_tmpl WHERE 1=1

  and company_id = #{company_id}


  and tmpl_type_id = #{tmpl_type_id}


  and tmpl_name LIKE  CONCAT('%','${tmpl_name}','%' )


  and tmpl_type_name LIKE  CONCAT('%','${tmpl_type_name}','%' )


  and status = #{status}
