mapper.select:
select

from pm_routing_tmpl_type WHERE 1=1

  and company_id = #{company_id}


  and tmpl_type_name LIKE  CONCAT('%','${tmpl_type_name}','%' )


  and status = #{status}

limit #{page}, #{page_size}

