mapper.select:
select id as shift_id, day_mins, shift_color, shift_name, shift_type
from pm_shift
where status = 1
  and company_id = #{companyId}
  and (#{query.shiftName} is null or #{query.shiftName} = '' or  shift_name LIKE CONCAT('%','${query.shiftName}','%'))
limit #{page},#{pageSize}

mapper.update:
update pm_shift
set status = 0, updated_at = #{updatedAt}
where id = #{shiftId} and shift_type = '2' and status = 1
