mapper.select:
select a.id, a.role, a.name, a.description, a.sort_value, a.is_active,
 DATE_FORMAT( a.updated_at,'%Y-%m-%d') created_at,   DATE_FORMAT( a.updated_at,'%Y-%m-%d') updated_at
from role as a,user_role as b
where a.id = b.role_id and  b.uid = #{uid,jdbcType=BIGINT}

  and b.company_id = #{companyId}

select       a.role as code, a.name,a.id as role_id
   from       role as a,user_role as b
    where     a.id = b.role_id and  b.uid = #{uid}  and b.company_id = #{companyId}
