mapper.select:
select id, phone, message, type, status, created_at, updated_at
from sms_send
where id = #{id,jdbcType=BIGINT}

mapper.insert:
insert into sms_send (phone, message, status)
values ( #{phone,jdbcType=VARCHAR}, #{message,jdbcType=VARCHAR},
#{status,jdbcType=TINYINT}
)

mapper.select:
select 
from sms_send
where is_active = 1 and phone = #{phone,jdbcType=VARCHAR}

mapper.update:
update sms_send
set is_active = 0
where phone = #{phone,jdbcType=VARCHAR}
